import { Identifiable, Sortable, State, UUID } from '@frugal-ui/base';
import { DirectoryItem } from './DirectoryItem';

export class IdentifiableDirectoryItem implements Identifiable, Sortable {
	uuid = new UUID();
	index = new State(0);

	name: string;
	isDirectory: boolean;

	constructor(item: DirectoryItem, index: number) {
		this.name = item.name;
		this.isDirectory = item.isDirectory;
		this.index.value = index;
	}
}
