import { DirectoryItemViewCfg } from './DirectoryItemView';
import { ItemOptionsMenu } from './ItemOptionsMenu';
import { Icon, Text } from '@frugal-ui/base';

export interface ItemTextCfg extends DirectoryItemViewCfg {
	itemName: string;
	isDirectory: boolean;
}

export function ItemText(configuration: ItemTextCfg) {
	return [
		ItemOptionsMenu(configuration),
		Icon(configuration.isDirectory ? 'folder' : 'note'),
		Text(configuration.itemName)
			.cssFontFamily('mono-rg')
			.useDefaultPadding()
			.cssWidth('100%')
			.cssFlex(1)
			.cssTextAlign('left'),
	];
}
