import { readdir, writeFile } from '@basicserver/fs-frontend';
import { Component } from '@frugal-ui/base';
import { DirectoryListing } from './Data/DirectoryItem';

export async function getDirectoryListing(path: string) {
	const listingText = await readdir(path);
	const listing: DirectoryListing = JSON.parse(listingText);
	const cleaned = listing.filter((item) => item.name[0] != '.');
	return cleaned;
}
